import React, { useState } from "react";
import { FaMicrophone } from "react-icons/fa";

// Extend the Window interface to include SpeechRecognition types
declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

interface FormData {
  name: string;
  age: string;
  gender: string;
  contact: string;
  address: string;
  symptoms: string;
  medication: string;
}

const allowedGenders = ["male", "female", "other"];

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

const isValidAge = (age: string) => {
  const numAge = parseInt(age, 10);
  return numAge >= 1 && numAge <= 120;
};

const UserForm: React.FC = () => {
  const [formData, setFormData] = useState<FormData>({
    name: "",
    age: "",
    gender: "",
    contact: "",
    address: "",
    symptoms: "",
    medication: "",
  });

  const handleSpeechInput = (field: keyof FormData) => {
    if (!SpeechRecognition) {
      alert("Speech recognition is not supported in this browser.");
      return;
    }

    const recog = new SpeechRecognition();
    recog.lang = "en-US";
    recog.interimResults = false;
    recog.maxAlternatives = 1;

    recog.onresult = (ev: any) => {
      let transcript = ev.results[0][0].transcript.trim().replace(/[.,]$/, "");
      if (field === "gender") {
        if (/mail/i.test(transcript)) transcript = "Male";
        else if (/femail|femal/i.test(transcript)) transcript = "Female";
        else if (/other/i.test(transcript)) transcript = "Other";
      }
      if (field === "age" || field === "contact") {
        transcript = transcript.replace(/\D/g, "");
      }
      setFormData((prev) => ({
        ...prev,
        [field]: transcript.charAt(0).toUpperCase() + transcript.slice(1),
      }));
    };

    recog.onerror = () => recog.stop();
    recog.start();
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!isValidAge(formData.age)) {
      alert("Please enter a valid age between 1 and 120.");
      return;
    }
    if (!allowedGenders.includes(formData.gender.toLowerCase())) {
      alert("Gender must be Male, Female, or Other.");
      return;
    }
    if (!/^\d{9,10}$/.test(formData.contact)) {
      alert("Contact number must be 9 or 10 digits.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!response.ok) throw new Error("Submission failed");
      const result = await response.json();
      console.log("Server response:", result);
      alert("Form submitted successfully!");
    } catch (error) {
      console.error("Submission error:", error);
      alert("Something went wrong while submitting the form.");
    }
  };

  const inputStyle = { flex: 1 };
  const labelStyle = { width: "150px", marginRight: "10px", color: "#197a74" };
  const fieldContainer = {
    display: "flex",
    alignItems: "center",
    marginBottom: "1rem",
    gap: "10px",
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        width: "100vw",
        height: "100vh",
        background: "#fff",
        position: "relative",
      }}
    >
      <img
        src="/clinicall-logo.png"
        alt="CLINI-CALL Logo"
        style={{
          position: "absolute",
          top: "20px",
          left: "20px",
          width: "150px",
          height: "auto",
        }}
      />

      <form
        onSubmit={handleSubmit}
        style={{
          maxWidth: "500px",
          width: "100%",
          padding: "20px",
          background: "#fff",
          borderRadius: "8px",
          boxShadow: "0px 4px 6px rgba(176, 39, 39, 0.1)",
          border: "2px solid #197a74",
        }}
      >
        <h2 style={{ textAlign: "center", color: "#197a74" }}>
          Patient Information
        </h2>

        {["name", "age", "contact", "address", "symptoms", "medication"].map(
          (key) => (
            <div key={key} style={fieldContainer}>
              <label htmlFor={key} style={labelStyle}>
                {key.charAt(0).toUpperCase() + key.slice(1)}
              </label>
              <input
                id={key}
                name={key}
                type="text"
                value={formData[key as keyof FormData]}
                onChange={handleChange}
                style={inputStyle}
              />
              <button
                type="button"
                onClick={() => handleSpeechInput(key as keyof FormData)}
                style={{
                  background: "transparent",
                  border: "none",
                  cursor: "pointer",
                  color: "#197a74",
                  fontSize: "20px",
                }}
                aria-label={`Speak ${key}`}
              >
                <FaMicrophone />
              </button>
            </div>
          )
        )}

        <div style={fieldContainer}>
          <label htmlFor="gender" style={labelStyle}>
            Gender
          </label>
          <select
            id="gender"
            name="gender"
            value={formData.gender}
            onChange={handleChange}
            style={inputStyle}
          >
            <option value="">Select Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
        </div>

        <button
          type="submit"
          style={{
            width: "100%",
            padding: "10px",
            fontSize: "16px",
            background: "#197a74",
            color: "#fff",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default UserForm;
