import React, { StrictMode, useState } from "react";
import { createRoot } from "react-dom/client";
import { FaMicrophone } from "react-icons/fa";

// Extend the Window interface to include SpeechRecognition types
declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

interface FormData {
  name: string;
  age: string;
  gender: string;
  contact: string;
  address: string;
  symptoms: string;
  medication: string;
}

// Age validation function
const isValidAge = (age: string) => {
  const numAge = parseInt(age, 10);
  return numAge >= 1 && numAge <= 120;
};

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;

const UserForm: React.FC = () => {
  const [formData, setFormData] = useState<FormData>({
    name: "",
    age: "",
    gender: "",
    contact: "",
    address: "",
    symptoms: "",
    medication: "",
  });

  const handleSpeechInput = (field: keyof FormData) => {
    if (!SpeechRecognition) {
      alert("Speech recognition is not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.start();

    recognition.onresult = (event: any) => {
      let spokenText = event.results[0][0].transcript.toLowerCase();
      spokenText = spokenText.replace(/[.,]$/, "");

      setFormData((prev) => ({
        ...prev,
        [field]: spokenText,
      }));
    };

    recognition.onerror = (event: any) => {
      console.error("Speech recognition error:", event.error);
    };
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
  e.preventDefault();

  if (!isValidAge(formData.age)) {
    alert("Please enter a valid age between 1 and 120.");
    return;
  }

  try {
    const response = await fetch("http://localhost:5000/api/users", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    });

    if (!response.ok) throw new Error("Submission failed");
    const result = await response.json();
    console.log("Server response:", result);
    alert("Form submitted successfully!");
  } catch (error) {
    console.error("Submission error:", error);
    alert("Something went wrong while submitting the form.");
  }
};


  const inputStyle = { flex: 1 };
  const labelStyle = { width: "150px", marginRight: "10px", color: "#197a74" };
  const fieldContainer = {
    display: "flex",
    alignItems: "center",
    marginBottom: "1rem",
    gap: "10px",
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        width: "100vw",
        height: "100vh",
        background: "#fff",
        position: "relative",
      }}
    >
      <img
        src="/clinicall-logo.png"
        alt="CLINI-CALL Logo"
        style={{
          position: "absolute",
          top: "20px",
          left: "20px",
          width: "150px",
          height: "auto",
        }}
      />

      <form
        onSubmit={handleSubmit}
        style={{
          maxWidth: "500px",
          width: "100%",
          padding: "20px",
          background: "#fff",
          borderRadius: "8px",
          boxShadow: "0px 4px 6px rgba(176, 39, 39, 0.1)",
          border: "2px solid #197a74",
        }}
      >
        <h2 style={{ textAlign: "center", color: "#197a74" }}>
          Patient Information
        </h2>

        {[
          ["name", "Name"],
          ["age", "Age"],
          ["contact", "Contact Number"],
          ["address", "Address"],
          ["symptoms", "Symptoms"],
          ["medication", "Any previous medications"],
        ].map(([key, label]) => (
          <div key={key} style={fieldContainer}>
            <label htmlFor={key} style={labelStyle}>
              {label}
            </label>
            <input
              id={key}
              name={key}
              type="text"
              value={formData[key as keyof FormData]}
              onChange={handleChange}
              style={inputStyle}
            />
            <button
              type="button"
              onClick={() => handleSpeechInput(key as keyof FormData)}
              style={{
                background: "transparent",
                border: "none",
                cursor: "pointer",
                color: "#197a74",
                fontSize: "20px",
              }}
              aria-label={`Speak ${label}`}
            >
              <FaMicrophone />
            </button>
          </div>
        ))}

        {/* Sex Select Field */}
        <div style={fieldContainer}>
          <label htmlFor="sex" style={labelStyle}>
            Sex
          </label>
          <select
            id="sex"
            name="sex"
            value={formData.gender}
            onChange={handleChange}
            style={inputStyle}
          >
            <option value="">Select Sex</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
        </div>

        <button
          type="submit"
          style={{
            width: "100%",
            padding: "10px",
            fontSize: "16px",
            background: "#197a74",
            color: "#fff",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default UserForm;
